    public function getLog() {        $content = @file_get_contents($this->file);        return $content;    }}			if (filter_var($email, FILTER_VALIDATE_EMAIL)) {		echo "<div class='col s12 green-text'>Thank you for your email. We will contact you soon.</div>";		$log = new Logger("log.txt");	$log->setTimestamp("D M d 'y h.i A");    $log->putLog($email.";".$name);	}else{		echo "<div class='col s12 red-text'>Email Invalid</div>";	}	exit;	}?><?php Header("Cache-Control: must-revalidate"); $offset = 60 * 60 * 24 * 3; $ExpStr = "Expires: " . gmdate("D, d M Y H:i:s", time() + $offset) . " GMT"; Header($ExpStr);?>